function randomizeArray($array) {\n    $count = count($array);\n    for ($i = $count - 1; $i > 0; $i--) {\n        // Generate a random index between 0 and $i\n        $j = mt_rand(0, $i);\n        // Swap elements at indices $i and $j\n        $temp = $array[$i];\n        $array[$i] = $array[$j];\n        $array[$j] = $temp;\n    }\n    return $array;\n}\n\n$originalArray = [1, 2, 3, 4, 5];\n$randomizedArray = randomizeArray($originalArray);\nprint_r($randomizedArray);