<?php\n// Load the source image\n$source_image = 'path/to/your/image.jpg';\n$image = imagecreatefromjpeg($source_image);\n\nif (!$image) {\n    die('Failed to load image.');\n}\n\n// Define the new dimensions\n$new_width = imagesx($image); // Same width as the original\n$new_height = 800; // Height of the cropped region\n\n// Create a true color image with the new dimensions\n$cropped_image = imagecreatetruecolor($new_width, $new_height);\n\n// Crop the original image to the top 800px and copy it to the new created image\nimagecopyresampled($cropped_image, $image, 0, 0, 0, 0, $new_width, $new_height, $new_width, $new_height);\n\n// Output the cropped image directly without saving\nheader('Content-Type: image/png');\nimagepng($cropped_image);\n\n// Free up memory\nimagedestroy($image);\nimagedestroy($cropped_image);\n?>