$items = [];\nforeach($query->result() as $content) {\n    $item = $this->{'mod_'.$content->type}->get($content->id);\n    if ($item === null) continue; // Safeguard against null returns\n\n    // Clone the object if it's important to have a separate instance\n    // $itemClone = clone $item;\n\n    print_r($item);\n\n    // Store a copy of the item in the array\n    $items[] = unserialize(serialize($item)); // Deep copy using serialize/unserialize\n}\n\nprint_r($items);