function getData() {\n    $location = "http://data.example.com/new-data.php";\n    $ch = curl_init($location);\n    curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);\n    $contents = curl_exec($ch);\n    if(curl_errno($ch)){\n        echo 'Request Error:' . curl_error($ch);\n    }\n    curl_close($ch);\n    $jsonVars = json_decode($contents, true);\n    return !empty($jsonVars) ? $jsonVars : null;\n}