function getPrimes($limit) {\n    if ($limit < 2) return [];\n\n    $sieve = array_fill(0, intval(($limit - 1) / 2), true);\n    $primes = [2];\n\n    for ($i = 3; $i <= sqrt($limit); $i += 2) {\n        if ($sieve[intval(($i - 3) / 2)]) {\n            for ($j = $i * $i; $j <= $limit; $j += 2 * $i) {\n                $sieve[intval(($j - 3) / 2)] = false;\n            }\n        }\n    }\n\n    for ($i = 0; $i < count($sieve); $i++) {\n        if ($sieve[$i]) {\n            $primes[] = 2 + (2 * $i);\n        }\n    }\n\n    return $primes;\n}\n\n$limit = 2000000;\n$primes = getPrimes($limit);