<?php\n\nfunction isRSSFeed($url) {\n    $ch = curl_init();\n    curl_setopt($ch, CURLOPT_URL, $url);\n    curl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);\n    curl_setopt($ch, CURLOPT_TIMEOUT, 5); // timeout after 5 seconds\n    $output = curl_exec($ch);\n    if(curl_errno($ch)) return false; // Error in request\n    $httpCode = curl_getinfo($ch, CURLINFO_HTTP_CODE);\n    curl_close($ch);\n\n    // Check HTTP code for success (200-299)\n    if ($httpCode < 200 || $httpCode >= 300) {\n        return false;\n    }\n\n    return strpos(trim(strip_tags($output)), '<rss') === 0 || strpos(trim(strip_tags($output)), 'xmlns:atom="http://www.w3.org/2005/Atom"') !== false;\n}\n\nfunction discoverRSSFeeds($url) {\n    $ch = curl_init();\n    curl_setopt($ch, CURLOPT_URL, $url);\n    curl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);\n    $output = curl_exec($ch);\n    curl_close($ch);\n\n    $dom = new DOMDocument;\n    @$dom->loadHTML($output); // Suppress errors with @\n    \n    $feeds = [];\n    foreach ($dom->getElementsByTagName('link') as $link) {\n        if (strtolower($link->getAttribute('type')) === 'application/rss+xml'\n            || strtolower($link->getAttribute('type')) === 'application/atom+xml'\n            || strpos(strtolower($link->getAttribute('href')), '.rss') !== false\n            || strpos(strtolower($link->getAttribute('href')), '.atom') !== false) {\n    \n                $feedUrl = $link->getAttribute('href');\n                // Resolve URL if it's relative\n                if (!filter_var($feedUrl, FILTER_VALIDATE_URL)) {\n                    $parsedUrl = parse_url($url);\n                    $feedUrl = ($parsedUrl['scheme'] ?? 'http') . '://' . $parsedUrl['host'] . $feedUrl;\n                }\n                $feeds[] = $feedUrl;\n            }\n    }\n\n    return $feeds;\n}\n\n$url = "https://techcrunch.com"; // Example URL\nif (isRSSFeed($url)) {\n    echo "The given URL is an RSS feed.";\n} else {\n    $feeds = discoverRSSFeeds($url);\n    if (!empty($feeds)) {\n        echo "Found potential RSS feeds:\n";\n        foreach ($feeds as $feed) {\n            echo "- " . htmlspecialchars($feed) . "\n";\n        }\n    } else {\n        echo "No RSS feed found for the given URL.";\n    }\n}