<?php\n// Define an associative array with your offers\n$offers = [\n    1 => 'Offer 1 Name',\n    2 => 'Offer 2 Name',\n    3 => 'Offer 3 Name',\n    // Add more offers as needed\n];\n\n// Retrieve the id from the GET request, defaulting to 1 if not set or invalid\n$id = isset($_GET['id']) && array_key_exists((int)$_GET['id'], $offers) ? (int)$_GET['id'] : 1;\n\n// Get the offer name based on the id\n$offername = $offers[$id];\n\n// Output the offer name\necho htmlspecialchars($offername);\n?>