ARTICLE_NO, $USERNAME, $ACCESSSTARTS, $ARTICLE_NAME, $shortDate);\n        \n        $rows = array();\n        while ($getRecords->fetch()) {\n            $rows[] = array(\n                'ARTICLE_NO' => $ARTICLE_NO,\n                'USERNAME' => $USERNAME,\n                'shortDate' => $shortDate,\n                'ACCESSSTARTS' => $ACCESSSTARTS,\n                'ARTICLE_NAME' => $ARTICLE_NAME\n            );\n        }\n        return $rows;\n    } else {\n        print_r($con->error);\n        return array();\n    }\n}\n\n$con->close(); // Close the database connection when done\n?>