function getFormData()\n{\n    $form_fields = array_keys($_POST);\n    $formData = array();\n\n    for ($i = 0; $i < sizeof($form_fields); $i++) {\n        $thisField = $form_fields[$i];\n        $thisValue = $_POST[$thisField];\n\n        // Initialize $str for each new field\n        if (is_array($thisValue)) {\n            $str = "";  // Initialize the string here\n\n            for ($j = 0; $j < sizeof($thisValue); $j++) {\n                $str .= "$thisValue[$j],";\n            }\n\n            // Remove the extra ',' at the end\n            if (!empty($str)) {  // Check to avoid warnings on empty strings\n                $thisValue = substr($str, 0, -1);\n            } else {\n                $thisValue = "";\n            }\n\n            // Associative array $variable[key] = value\n            $formData[end(explode("_", $thisField))] = $thisValue;\n        } else {\n            $formData[end(explode("_", $thisField))] = $thisValue;\n        }\n    }\n\n    return $formData;\n}