function formatFloat($number) {\n    // Check if the number is an integer by comparing it with its rounded value\n    if ($number == intval($number)) {\n        return number_format($number); // No decimal places\n    } else {\n        return number_format($number, 1); // One decimal place for non-integers\n    }\n}\n\n// Example usage:\necho formatFloat(2.5); // Outputs: 2.5\necho formatFloat(3.0); // Outputs: 3