<?php\n$pdo = new PDO('mysql:host=your_host;dbname=your_db', 'username', 'password');\n$stmt = $pdo->prepare(\n    "SELECT \n     (SELECT MAX(id) FROM your_table WHERE id < ?) AS prev_id,\n     (SELECT MIN(id) FROM your_table WHERE id > ?) AS next_id"\n);\n$id = 9; // Example current ID\n$stmt->execute([$id, $id]);\n$result = $stmt->fetch();\n$prevId = $result['prev_id'];\n$nextId = $result['next_id'];\n?>