<?php\n\nrequire 'vendor/autoload.php';\n\nuse PhpOffice\PhpSpreadsheet\IOFactory;\nuse PhpOffice\PhpSpreadsheet\Spreadsheet;\n\n// Create a new Spreadsheet object\n$spreadsheet = new Spreadsheet();\n$sheet = $spreadsheet->getActiveSheet();\n\n// Set some data to test the functionality (e.g. column headings)\n$sheet->setCellValue('A1', 'ID');\n$sheet->setCellValue('B1', 'Name');\n\n// Setting specific values\n$sheet->setCellValueExplicit('E2', 'This is a test string', \PhpOffice\PhpSpreadsheet\Cell\DataType::TYPE_STRING);\n\n// Set headers for browser to download the file\nheader('Content-Type: application/vnd.openxmlformats-officedocument.spreadsheetml.sheet');\nheader('Content-Disposition: attachment;filename="YourFileName.xlsx"');\n\n$writer = IOFactory::createWriter($spreadsheet, 'Xlsx');\n$writer->save('php://output');