<?php\n\nrequire 'vendor/autoload.php';\n\nuse PhpMimeMailParser\Parser;\nuse PhpMimeMailParser\Attachment;\n\n$emailContent = "Your raw email content as a string"; // Replace with your actual email content\n\n$parser = new Parser();\n$parser->setText($emailContent);\n\n// Check if there are any attachments\nif ($parser->getAttachments()) {\n    $attachments = $parser->getAttachments();\n\n    foreach ($attachments as $attachment) {\n        /** @var Attachment $attachment */\n        $filename = $attachment->getFilename();\n        $filePath = 'path/to/save/' . $filename;\n\n        // Save the attachment\n        file_put_contents($filePath, $attachment->getContent());\n        echo "Attachment saved: " . $filePath . PHP_EOL;\n    }\n} else {\n    echo "No attachments found.";\n}