class ClassA{\n    static function call($class) {\n        return $class::inner();\n    }\n\n    static function inner(){\n        return "Class A";\n    }   \n}\n\nclass ClassB extends ClassA{\n    static function inner(){\n        return "Class B";\n    }\n}\n\necho "<p>Class A = " . ClassA::call('ClassA');\necho "<p>Class B = " . ClassB::call('ClassB');