function SaveXml()\n{\n    $doc = new DOMDocument('1.0', 'utf-8');\n    $rss = $doc->createElement('rss');\n    $rss->setAttribute('version', '2.0');\n    $rss->setAttribute('xmlns:dc', 'http://purl.org/dc/elements/1.1/');\n    $doc->appendChild($rss);\n\n    $channel = $doc->createElement('channel');\n    $title = $doc->createElement('title', htmlspecialchars($this->getProjectName()));\n    $link = $doc->createElement('link', 'http://www.mywebsite.com');\n    $description = $doc->createElement('description', 'My description');\n    $lastBuildDate = $doc->createElement('lastBuildDate', date(DATE_RFC822));\n\n    $channel->appendChild($title);\n    $channel->appendChild($link);\n    $channel->appendChild($description);\n    $channel->appendChild($lastBuildDate);\n\n    foreach ($this->arrData as $value) {\n        $item = $doc->createElement('item');\n\n        $itemTitle = $doc->createElement('title', htmlspecialchars($value[0]));\n        $pubDate = $doc->createElement('pubDate', date(DATE_RFC822, strtotime($value[1])));\n        $creator = $doc->createElement('dc:creator', htmlspecialchars($value[2]));\n        $description = $doc->createElement('description');\n        $descriptionText = $doc->createCDATASection(htmlspecialchars($value[3]));\n        $itemLink = $doc->createElement('link');\n        $linkText = $doc->createCDATASection(htmlspecialchars($value[4]));\n        $guid = $doc->createElement('guid', htmlspecialchars($value[4]));\n        $category = $doc->createElement('category', 'Mycategory');\n\n        $description->appendChild($descriptionText);\n        $itemLink->appendChild($linkText);\n\n        $item->appendChild($itemTitle);\n        $item->appendChild($pubDate);\n        $item->appendChild($creator);\n        $item->appendChild($description);\n        $item->appendChild($itemLink);\n        $item->appendChild($guid);\n        $item->appendChild($category);\n\n        $channel->appendChild($item);\n    }\n\n    $rss->appendChild($channel);\n\n    // Save the XML file\n    $doc->formatOutput = true; // This formats the XML for readability\n    file_put_contents($this->getFileName(), $doc->saveXML());\n}