// Assuming $response is the response object from Tarzan AWS\n$body = $response->body;\n$queryResult = $body->QueryWithAttributesResult;\n\nforeach ($queryResult->Item as $item) {\n    $itemName = (string)$item->Name; // Get the name of the item\n    echo "Item Name: $itemName<br>";\n\n    foreach ($item->Attribute as $attribute) {\n        $attrName = (string)$attribute->Name;\n        $attrValue = (string)$attribute->Value;\n        echo "$attrName: $attrValue<br>";\n    }\n    echo "<hr>"; // Separator between items\n}