function simplexml_to_array($simplexml) {\n    $return = [];\n    if (count($simplexml->children()) == 0) {\n        return (string)$simplexml;\n    }\n    \n    foreach ($simplexml->children() as $child => $node) {\n        $data = simplexml_to_array($node);\n        if (!empty($node->attributes())) {\n            foreach ($node->attributes() as $attr => $value) {\n                $data['@' . $attr] = (string)$value;\n            }\n        }\n\n        $return[$child][] = $data;\n\n        // Remove unnecessary index if only one child exists\n        if (count($return[$child]) == 1) {\n            $return[$child] = $return[$child][0];\n        }\n    }\n\n    return $return;\n}\n\n$arrayData = simplexml_to_array($response->body);\n// Now you can use $arrayData as a regular PHP array