try {\n    $path = "c:/xampp/htdocs/";\n    com_load_typelib('Word.Application');\n    $word = new COM("word.application") or die("Unable to instantiate Word");\n    $word->Visible = false;  // Run silently\n\n    $document = $word->Documents->Open($path."tmp/invoice.doc");\n\n    $bookmarkname = "InvoiceNo";\n    $objBookmark  = $document->Bookmarks($bookmarkname)->Range->Text = "INVOICE ID";\n\n    $tempSavePath = tempnam(sys_get_temp_dir(), 'PHPWord');\n    $document->SaveAs($tempSavePath);\n\n    // Close the document and Word application\n    $document->Close(false);\n    $word->Quit();\n    $document = null;\n    $word = null;\n\n    // Optionally move or rename the saved file to your desired location\n    if (file_exists($tempSavePath)) {\n        copy($tempSavePath, $path."tmp/invoice2.doc");\n        unlink($tempSavePath);  // Clean up temp file\n    } else {\n        echo "Failed to save the document.";\n    }\n\n} catch (Exception $e) {\n    echo 'Caught exception: ', $e->getMessage(), "\n";\n}