class UserController {\n    public function renderView() {\n        // Assume there's some logic to create models, get pagination, etc.\n        $data = [\n            'models' => $models,\n            'currentPage' => $current,\n            'totalPages' => $total,\n        ];\n        \n        return $view->render($data);\n    }\n}\n\nclass UserView {\n    public function render(array $data) {\n        // Extract variables from the array\n        extract($data);\n\n        // Render the view using extracted variables\n        // For example, you can use a template engine like Twig or PHP's built-in templating\n        // Here is a simple example with PHP:\n        ?>\n        <div>\n            <?php foreach ($models as $model): ?>\n                <!-- User data rendering here -->\n            <?php endforeach; ?>\n        </div>\n        <p>Page: <?php echo htmlspecialchars($currentPage); ?> of <?php echo htmlspecialchars($totalPages); ?></p>\n        <?php\n    }\n}