// Using an improved class\nclass Person {\n    private $name;\n    private $height; // in cm or any preferred unit\n    private $weight; // in kg or any preferred unit\n\n    public function __construct($name, $height, $weight) {\n        $this->name   = $name;\n        $this->height = $height;\n        $this->weight = $weight;\n    }\n\n    // Getters and setters\n    public function getName() { return $this->name; }\n    public function getHeight() { return $this->height; }\n    public function getWeight() { return $this->weight; }\n\n    public function setName($name) { $this->name = $name; }\n    public function setHeight($height) { $this->height = $height; }\n    public function setWeight($weight) { $this->weight = $weight; }\n}\n\n$people[1] = new Person('Bob', 10, 20);\n$people[2] = new Person('Fred', 15, 10);\n$people[3] = new Person('Ned', 25, 30);\n\n// Accessing data\necho $people[1]->getName(); // Outputs: Bob