<?php\n\nfunction distance($lat1, $lon1, $lat2, $lon2) {\n    $earthRadius = 3963.191; // miles\n    $dLat = deg2rad($lat2 - $lat1);\n    $dLon = deg2rad($lon2 - $lon1);\n\n    $a = sin($dLat/2) * sin($dLat/2) + cos(deg2rad($lat1)) * cos(deg2rad($lat2)) *\n         sin($dLon/2) * sin($dLon/2);\n    $c = 2 * atan2(sqrt($a), sqrt(1-$a));\n\n    return $earthRadius * $c;\n}\n\nfunction doCirclesOverlap($lat1, $lon1, $r1, $lat2, $lon2, $r2) {\n    $d = distance($lat1, $lon1, $lat2, $lon2);\n    return ($r1 + $r2) >= $d;\n}\n\n// Example usage\n$searchCenterLat = 37.7749; // Latitude of search center (e.g., San Francisco)\n$searchCenterLon = -122.4194; // Longitude of search center\n$radius = 10; // Radius of interest from the search center in KM\n\n$objLat = 38.2583;\n$objLon = -122.6547;\n$objServiceRadius = 10;\n\nif (doCirclesOverlap($searchCenterLat, $searchCenterLon, $radius, $objLat, $objLon, $objServiceRadius)) {\n    echo "Object is within range or provides service to the search area.\n";\n} else {\n    echo "Object is not within range and does not provide service to the search area.\n";\n}