<?php\nheader('Content-Type: image/png');\n\n// Path to your TTF file that supports East Asian characters\n$font = 'path/to/MicrosoftYaHei.ttf';\n\n$image = imagecreatetruecolor(400, 200);\n$white = imagecolorallocate($image, 255, 255, 255);\n$black = imagecolorallocate($image, 0, 0, 0);\n\n// Fill the background of the image with white\nimagefilledrectangle($image, 0, 0, 399, 199, $white);\n\n$text = 'こんにちは世界'; // Sample text in Japanese\n\n// Add some text to the image\nimagettftext($image, 40, 0, 50, 120, $black, $font, $text);\n\n// Output the image\nimagepng($image);\n\n// Free up memory\nimagedestroy($image);\n?>