<?php\n    $recipient = "example@gmail.com"; // Ensure this is a valid email address\n    $senderName = "Your Name";\n    $senderEmail = "welcome@yourdomain.com"; // Use a domain you own and have configured SPF/DKIM/DMARC for\n    $message = "Hello, this is a test message.";\n    $subject = "Subject: Greeting from " . htmlspecialchars($senderName);\n    \n    $headers  = 'MIME-Version: 1.0' . "\r\n";\n    $headers .= 'Content-type: text/plain; charset=UTF-8' . "\r\n";\n    $headers .= 'From: ' . $senderName . ' <' . $senderEmail . '>' . "\r\n";\n\n    mail($recipient, $subject, $message, $headers);\n?>