class ShoppingCartItem extends Item {\n    private $quantity;\n    private $cart_id;\n    private $price;\n\n    public function __construct(mysqli $db, $id = null) {\n        parent::__construct($db, $id);\n        $this->quantity = 1; // Default quantity\n        $this->cart_id = null;\n        $this->price = 0.0; // Default price\n    }\n\n    // Getter for quantity\n    public function getQuantity() {\n        return $this->quantity;\n    }\n\n    // Setter for quantity\n    public function setQuantity($quantity) {\n        if ($quantity > 0) { // Validation to ensure positive quantities\n            $this->quantity = $quantity;\n        }\n    }\n\n    // Getter for cart_id\n    public function getCartId() {\n        return $this->cart_id;\n    }\n\n    // Setter for cart_id\n    public function setCartId($cart_id) {\n        $this->cart_id = $cart_id;\n    }\n\n    // Getter for price\n    public function getPrice() {\n        return $this->price;\n    }\n\n    // Setter for price\n    public function setPrice($price) {\n        if ($price >= 0) { // Validation to ensure non-negative price\n            $this->price = $price;\n        }\n    }\n}\n\n// Usage example\n$dbConnection = new mysqli('host', 'user', 'password', 'database');\n$cartItem = new ShoppingCartItem($dbConnection, 1);\n$cartItem->setQuantity(3);\n$cartItem->setPrice(19.99);