$pages = array("grac", "zamknij", "dolaczyc");\n$pagesid = array("showNews", "showThread", "showProfile");\n\n// Check for $pagesid first\nforeach ($pagesid as $page) {\n    if (isset($_GET[$page])) {\n        include('sobra/'.$page.'.php');\n        exit; // Include this to stop processing further if a match is found\n    }\n}\n\n// Then check just pages\nif (isset($_GET['page']) && in_array($_GET['page'], $pages)) {\n    include("sobra/".$_GET['page'].".php"); // Ensure the path is consistent\n} else {\n    // error\n    include('error.php');\n}