// Start the session\nsession_start();\n\nif (isset($_POST['items'])) {\n    // Merge new items from POST only where they are not empty\n    foreach ($_POST['items'] as $key => $value) {\n        if (!empty($value)) { // Check if the value is not empty\n            $_SESSION['items'][$key] = $value; // Update session array with non-empty values\n        }\n    }\n} else {\n    // Initialize session 'items' array if it's not already set\n    $_SESSION['items'] = $_SESSION['items'] ?? [];\n}\n\n// Now, $_SESSION['items'] contains the merged data without overwriting empty fields from POST