$query = "SELECT u.url_id, url, title, description, \n        DATE_FORMAT(STR_TO_DATE(pub_date, '%d-%b-%Y'), '%Y-%m-%d') AS pub_date \n    FROM urls AS u\n    JOIN url_associations AS ua ON u.url_id = ua.url_id \n    WHERE ua.url_category_id=$type AND ua.approved = 'Y' \n    ORDER BY STR_TO_DATE(pub_date, '%d-%b-%Y') DESC";\n$result = mysql_query($query);\n\nwhile ($row = mysql_fetch_assoc($result)) {\n    echo "  <tr>\n<td align=\"left\">{$row['pub_date']}</td>\n</tr>\n";\n}