<?php\n// Assuming you have established a connection to the MySQL database using mysqli or PDO\n\n$query = "\nSELECT \n    cg.cat_group_id, \n    cg.cat_group_name,\n    c.category_id, \n    c.title AS category_title,\n    COUNT(n.news_id) AS news_count\nFROM \n    CategoryGroups cg\nLEFT JOIN \n    Categories c ON cg.cat_group_id = c.cat_group_id\nLEFT JOIN \n    News n ON c.category_id = n.category_id\nGROUP BY \n    cg.cat_group_id, \n    c.category_id";\n\n$result = mysqli_query($connection, $query);\n\n$currentGroup = null;\nwhile ($row = mysqli_fetch_assoc($result)) {\n    if ($currentGroup !== $row['cat_group_id']) {\n        echo "<h3>{$row['cat_group_name']}</h3>";\n        $currentGroup = $row['cat_group_id'];\n    }\n    echo "{$row['category_title']} ({$row['news_count']})<br/>\n";\n}\n?>