class OriginalClass {\n    var $property1;\n    var $property2;\n\n    function __construct($prop1, $prop2) {\n        $this->property1 = $prop1;\n        $this->property2 = $prop2;\n    }\n}\n\nclass SubClass extends OriginalClass {\n    var $newProperty;\n\n    function setNewProperty($value) {\n        $this->newProperty = $value;\n    }\n}\n\n// Create an instance of the original class\n$originalObject = new OriginalClass('value1', 'value2');\n\n// Manually copy properties to a SubClass object\n$subClassObject = new SubClass($originalObject->property1, $originalObject->property2);\n\n// Optionally set additional properties specific to SubClass\n$subClassObject->setNewProperty('newValue');