<?php\n\nclass Product {\n    private $pdo;\n    private $stmt;\n\n    public function __construct(PDO $pdo) {\n        $this->pdo = $pdo;\n    }\n\n    public function getPrice($id) {\n        if (!$this->stmt) {\n            $this->stmt = $this->pdo->prepare('SELECT price FROM products WHERE id = ?');\n        }\n        $this->stmt->execute(array($id));\n        return $this->stmt->fetchColumn(0);\n    }\n}\n\n// example usage:\n$pdo = new PDO('your_dsn', 'username', 'password'); // Replace with actual connection details\n$productManager = new Product($pdo);\n$price = $productManager->getPrice(4982);\necho "Product 4982 costs $price\n";