class MyClass {\n    // Declare a static property\n    public static $sharedProperty;\n\n    // Method to update the static property\n    public function updateSharedProperty($value) {\n        self::$sharedProperty = $value;\n    }\n\n    // Method to get the value of the static property\n    public function getSharedProperty() {\n        return self::$sharedProperty;\n    }\n}\n\n// Usage example:\n$obj1 = new MyClass();\n$obj2 = new MyClass();\n\nMyClass::$sharedProperty = "Initial Value";\n\necho $obj1->getSharedProperty(); // Outputs: Initial Value\necho $obj2->getSharedProperty(); // Outputs: Initial Value\n\n$obj1->updateSharedProperty("Updated Value");\n\necho $obj1->getSharedProperty(); // Outputs: Updated Value\necho $obj2->getSharedProperty(); // Outputs: Updated Value