$searchTerm = 'banana'; // This should be dynamically set based on user input\n\nforeach ($list as $r) {\n    if (stripos($r[1], $searchTerm) !== 0) {\n        continue; // Skip to the next iteration if the product type does not start with 'banana'\n    }\n\n    $row_color = ($row_count % 2) ? $color1 : $color2;\n    $size2 = $r[2];\n    echo "<tr>\n        <td id=\"id\"><span id=\"non_sorting_header\">" . htmlspecialchars($r[0]) . "</span></td>\n        <td id=\"name\"><span id=\"non_sorting_header\">" . htmlspecialchars($r[1]) . "</span></td>\n        <td id=\"speed\"><span id=\"sorting_header\">" . kMGTB2($size2) . "</span></td>\n        <td id=\"download\"><span id=\"sorting_header\">" . $r[3] . " Gb<br />per month</span></td>\n        <td id=\"contract\"><span id=\"sorting_header\">1<br />month</span></td>\n        <td id=\"info\"><span id=\"non_sorting_header\">" . htmlspecialchars($r[5]) . "</span></td>\n        <td id=\"buy\"><span id=\"non_sorting_header\">" . htmlspecialchars($r[4]) . "<br />" . htmlspecialchars($r[6]) . "</span></td>\n      </tr>";\n    $row_count++;\n}