<?php\n// receive.php\nheader("Content-Type: application/json");\n\ndate_default_timezone_set('UTC'); // Set your desired timezone\n\n$data = $_POST['data'] ?? 'No Data';\n\nif (!empty($data)) {\n    $filename = "received_data.txt";\n    $fileHandle = fopen($filename, 'a');\n    if ($fileHandle) {\n        fwrite($fileHandle, date("Y-m-d H:i:s") . ": Received data - " . htmlspecialchars($data) . PHP_EOL);\n        fclose($fileHandle);\n\n        echo json_encode(["success" => true, "message" => "Data recorded successfully."]);\n    } else {\n        echo json_encode(["success" => false, "message" => "Failed to open file for writing."]);\n    }\n} else {\n    echo json_encode(["success" => false, "message" => "No data received."]);\n}\n?>