<?php\n\nfunction svnMove($source, $destination) {\n    // Delete the destination if it exists\n    if (is_dir($destination)) {\n        exec("svn delete {$destination}");\n        rmdir($destination);\n    }\n    // Rename/move the directory using PHP\n    rename($source, $destination);\n    // SVN move command to reflect in version control\n    exec("svn move {$source} {$destination}");\n}\n\nfunction svnDelete($directory) {\n    // Delete directory from file system and version control\n    if (is_dir($directory)) {\n        exec("svn delete {$directory}");\n        rmdir($directory);\n    }\n}\n\nfunction svnAdd($fileOrDirectory) {\n    // Add new files/directory to version control\n    if (file_exists($fileOrDirectory)) {\n        exec("svn add {$fileOrDirectory}");\n    }\n}\n\n// Usage example:\nsourceDir = 'somedir/foo';\ndestinationDir = 'somedir/modified';\n\nsvnMove(sourceDir, destinationDir);\n\n?>