<?php\nif(isset($_POST['city_id']) && isset($_POST['area_id'])) {\n    $cityId = intval($_POST['city_id']);\n    $areaId = intval($_POST['area_id']);\n    require 'db_connection.php';\n\n    // Retrieve results based on city ID and area ID\n    $query = "SELECT result_title FROM tbl_results WHERE result_city = ? AND result_area = ?";\n    $stmt = $conn->prepare($query);\n    $stmt->bind_param("ii", $cityId, $areaId); // Bind both variables to the prepared statement as integers\n    $stmt->execute();\n    $result = $stmt->get_result();\n\n    while ($row = $result->fetch_assoc()) {\n        echo htmlspecialchars($row['result_title']) . "<br>";\n    }\n}\n$stmt->close();\n$conn->close();\n?>