class Application_Model_DbTable_Users extends Zend_Db_Table_Abstract {\n    protected $_name = 'users';\n\n    // Define dependent tables (tables with foreign key pointing to users)\n    protected $_dependentTables = array(\n        'Application_Model_DbTable_UserPassword',\n        'Application_Model_DbTable_UserMetadata'\n    );\n\n    // Define reference map for easier joining with related tables\n    protected $_referenceMap = array(\n        'UserPassword' => array(\n            'columns'           => 'id',\n            'refTableClass'     => 'Application_Model_DbTable_UserPassword',\n            'refColumns'        => 'user_id'\n        ),\n        'UserMetadata' => array(\n            'columns'           => 'id',\n            'refTableClass'     => 'Application_Model_DbTable_UserMetadata',\n            'refColumns'        => 'user_id'\n        )\n    );\n\n    public function getUserWithPassword($userId) {\n        $select = $this->select()\n                       ->setIntegrityCheck(false)\n                       ->from(array('u' => 'users'))\n                       ->joinLeft(\n                           array('p' => 'user_password'),\n                           'u.id = p.user_id'\n                       )\n                       ->where('u.id = ?', $userId);\n        return $this->fetchRow($select)->toArray();\n    }\n\n    public function getUserWithMetadata($userId) {\n        $select = $this->select()\n                       ->setIntegrityCheck(false)\n                       ->from(array('u' => 'users'))\n                       ->joinLeft(\n                           array('m' => 'user_metadata'),\n                           'u.id = m.user_id'\n                       )\n                       ->where('u.id = ?', $userId);\n        return $this->fetchRow($select)->toArray();\n    }\n\n    // Add more methods as needed, like getUserWithPasswordIfDefaultAuth\n}\n\nclass Application_Model_DbTable_UserPassword extends Zend_Db_Table_Abstract {\n    protected $_name = 'user_password';\n\n    // Define reference map for easy joining with the users table\n    protected $_referenceMap = array(\n        'User' => array(\n            'columns'           => 'user_id',\n            'refTableClass'     => 'Application_Model_DbTable_Users',\n            'refColumns'        => 'id'\n        )\n    );\n}\n\nclass Application_Model_DbTable_UserMetadata extends Zend_Db_Table_Abstract {\n    protected $_name = 'user_metadata';\n\n    // Define reference map for easy joining with the users table\n    protected $_referenceMap = array(\n        'User' => array(\n            'columns'           => 'user_id',\n            'refTableClass'     => 'Application_Model_DbTable_Users',\n            'refColumns'        => 'id'\n        )\n    );\n}