$postId = 123; // Example post ID\n\n$query = "SELECT p2.id, p2.title, COUNT(ptt1.tag_id) AS common_tag_count\n          FROM Posts p1\n          JOIN posts_to_tags_joining_table ptt1 ON p1.id = ptt1.post_id\n          JOIN posts_to_tags_joining_table ptt2 ON ptt1.tag_id = ptt2.tag_id\n          JOIN Posts p2 ON ptt2.post_id = p2.id\n          WHERE p1.id = ? AND p2.id != p1.id\n          GROUP BY p2.id, p2.title\n          ORDER BY common_tag_count DESC";\n\n$stmt = $mysqli->prepare($query);\n$stmt->bind_param("i", $postId); // 'i' stands for integer\n$stmt->execute();\n$result = $stmt->get_result();\n\nwhile ($row = $result->fetch_assoc()) {\n    echo "Post ID: {$row['id']}, Title: {$row['title']}, Common Tags: {$row['common_tag_count']}<br>";\n}\n\n$stmt->close();