<?php\n// Set the character encoding for the database connection using utf8mb4\n$mysqli = new mysqli('localhost', 'user', 'password', 'database');\nif ($mysqli->connect_error) {\n    die("Connection failed: " . $mysqli->connect_error);\n}\n$mysqli->set_charset('utf8mb4');\n\n// Make sure to set the content type header to UTF-8\nheader('Content-Type: text/html; charset=utf-8');\n\n// Example of inserting data into the database\n$stmt = $mysqli->prepare("INSERT INTO users (name, email) VALUES (?, ?)");\n$stmt->bind_param("ss", $username, $email);\n$username = "José";\n$email = "jose@example.com";\n$stmt->execute();\n$stmt->close();\n\n// Fetching data example:\n$result = $mysqli->query("SELECT name FROM users WHERE email='jose@example.com'");\nwhile ($row = $result->fetch_assoc()) {\n    echo htmlspecialchars($row['name']); // Use htmlspecialchars to output names safely\n}\n\n$mysqli->close();\n?>