<?php\nfunction roundCorners($src, $radius, $dst = null) {\n    $img = imagecreatefromjpeg($src);\n    $w = imagesx($img);\n    $h = imagesy($img);\n\n    // create a new true color image\n    $newImg = imagecreatetruecolor($w, $h);\n    $transparentColor = imagecolorallocatealpha($newImg, 255, 255, 255, 127);\n    imagefill($newImg, 0, 0, $transparentColor);\n\n    // create a mask\n    $mask = imagecreatetruecolor($w, $h);\n    $transparentMaskColor = imagecolorallocatealpha($mask, 255, 255, 255, 127);\n    imagefill($mask, 0, 0, $transparentMaskColor);\n\n    // draw rounded corners\n    imagefilledellipticarc($mask, $radius, $radius, $radius * 2, $radius * 2, 90, 180, $black = imagecolorallocatealpha($mask, 0, 0, 0, 0));\n    imagefilledellipticarc($mask, $w - $radius, $h - $radius, $radius * 2, $radius * 2, 0, 90, $black);\n    imagefilledellipticarc($mask, $w - $radius, $radius, $radius * 2, $radius * 2, 180, 90, $black);\n    imagefilledellipticarc($mask, $radius, $h - $radius, $radius * 2, $radius * 2, 90, 0, $black);\n\n    // fill the center and corners of mask with black\n    imagefilltoborder($mask, intval($radius) + 1, intval($radius) + 1, $transparentMaskColor, $black);\n    imagesavealpha($mask, true);\n    imagesavealpha($newImg, true);\n\n    // copy the original image to new using mask for alpha channel\n    $result = imagecopy($newImg, $img, 0, 0, 0, 0, $w, $h);\n    imagealphablending($newImg, false);\n    imagesavealpha($newImg,true);\n\n    // apply mask\n    imagecopymerge($newImg, $mask, 0, 0, 0, 0, $w, $h, 100);\n\n    imagedestroy($img);\n    imagedestroy($mask);\n\n    if ($dst) {\n        return imagejpeg($newImg, $dst);\n    } else {\n        return imagejpeg($newImg);\n    }\n}\n\nfunction resizeImage($srcPath, $dstPath, $width, $height) {\n    list($originalWidth, $originalHeight) = getimagesize($srcPath);\n    $thumb = imagepcreatetruecolor($width, $height);\n    $source = imagecreatefromjpeg($srcPath);\n\n    imagecopyresampled($thumb, $source, 0, 0, 0, 0, $width, $height, $originalWidth, $originalHeight);\n    imagejpeg($thumb, $dstPath);\n\n    imagedestroy($thumb);\n    imagedestroy($source);\n}\n\n// Example usage\nroundCorners("path/to/image.jpg", 20, "path/to/rounded_image.jpg");\nresizeImage("path/to/image.jpg", "path/to/resized_image.jpg", 300, 200);