<?php\n\nrequire 'vendor/autoload.php';\n\nuse GeoIp2\Database\Reader;\n\ntry {\n    // This creates the Reader object, which should be reused across requests.\n    $reader = new Reader('/path/to/GeoLite2-Country.mmdb');\n\n    // Replace with the IP address you want to look up\n    $ipAddress = $_SERVER['REMOTE_ADDR'];\n\n    // Look up the country data for an IP address\n    $record = $reader->country($ipAddress);\n\n    echo 'Country ISO Code: ', $record->country->isoCode, "\n"; // e.g., "US"\n    echo 'Country Name: ', $record->country->name, "\n";       // e.g., "United States"\n\n    // Close the reader to clean up resources.\n    $reader->close();\n} catch (Exception $e) {\n    // Handle any errors\n    echo 'Error:', $e->getMessage(), "\n";\n}