if ($getFiles = $con->prepare($filesQuery)) {\n    $getFiles->bind_param("s", $pk);\n    $getFiles->execute();\n    $getFiles->bind_result($FILENAME);\n    $files = array();\n\n    while ($getFiles->fetch()) {\n        $filename = array(\n            'FILENAME' => htmlspecialchars($FILENAME), // Sanitize the filename\n        );\n        $files[] = $filename;\n    }\n}\n\n$filesList = '';\nforeach ($files as $filenames) {\n    $filesList .= '<p><a href="./files/' . $filenames['FILENAME'] . '">' . $filenames['FILENAME'] . '</a></p>' . "\n";\n}