<?php\n\nfunction stripNamespace($xml) {\n    $dom = new DOMDocument;\n    $dom->loadXML($xml);\n\n    // Remove namespaces\n    foreach ($dom->getElementsByTagName("*") as $node) {\n        if ($node->hasAttributes()) {\n            $attributes = $node->attributes();\n            for ($i = $attributes->length; --$i >= 0;) {\n                $attrNode = $attributes->item($i);\n                if (strpos($attrNode->name, 'xmlns') !== false) {\n                    $node->removeAttributeNode($attrNode);\n                }\n            }\n        }\n\n        // Rename node to have no namespace\n        $localName = $dom->createDocumentFragment();\n        $localName->appendXML('<' . str_replace(' ', '', $node->nodeName) . '/>');\n        $newNode = $dom->importNode($localName->firstChild, true);\n        foreach ($node->attributes as $attr) {\n            $newNode->appendChild($attr->cloneNode());\n        }\n        foreach ($node->childNodes as $childNode) {\n            $importedNode = $dom->importNode($childNode, true);\n            $newNode->appendChild($importedNode);\n        }\n\n        $node->parentNode->replaceChild($newNode, $node);\n    }\n\n    return $dom->saveXML();\n}\n\n$xmlString = file_get_contents('yourfile.xml');\n$cleanXmlString = stripNamespace($xmlString);\n\necho $cleanXmlString;\n\n?>