// Instead of using define, consider a Config class or array:\nclass Config {\n    public static function get($key) {\n        $config = [\n            'database_host' => 'localhost',\n            // other config options\n        ];\n        return $config[$key] ?? null;\n    }\n}\n\n// For $_GET handling, pass the value as an argument:\nclass UserProcessor {\n    public function processUserInput(?string $input) {\n        // Handle input safely here\n    }\n}\n\n// Usage example:\n$input = isset($_GET['username']) ? filter_input(INPUT_GET, 'username', FILTER_SANITIZE_STRING) : null;\n$userProcessor = new UserProcessor();\n$userProcessor->processUserInput($input);