function toAscii($str) {\n    // Transliterate UTF-8 string into ASCII\n    $str = iconv('UTF-8', 'ASCII//TRANSLIT', $str);\n    \n    // Replace any remaining unwanted characters\n    $str = preg_replace('/[^a-zA-Z0-9\-\_]/', '', $str);\n\n    return strtolower($str); // Optional: convert to lowercase if needed\n}\n\n$inputString = "lärm andré";\necho toAscii($inputString); // Outputs: larm andre