function print_it($item) {\n    echo '<div class="listItem clearfix">';\n    echo '<div class="info">';\n    echo '<span class="title">';\n    if(isset($item[6])) {\n        // Decode the URL component that was encoded with rawurlencode\n        $decodedUrl = substr(rawurldecode($item[6]), 13);\n        echo '<a href="http://' . $decodedUrl . '" title="' . htmlspecialchars($item[0]) . '">' . htmlspecialchars($item[0]) . '</a>';\n    }\n    echo '</span>';\n    echo '</div></div>';\n}