if( isset($_POST['submit']) ) {\n    $browser = $_SERVER['HTTP_USER_AGENT'];\n    $page = $_POST['page'];\n    $email = $_POST['email'];\n    $error = $_POST['error'];\n\n    $message  = "<html><body>\n";   \n    $message .= "Email: " . htmlspecialchars($email) . "\n";\n    $message .= "Page: " . htmlspecialchars($page) . "\n";\n    $message .= "OS/ Browser: " . htmlspecialchars($browser) . "\n";\n    $message .= "Error: " . htmlspecialchars($error) . "\n";\n    $message .= "</body></html>\n";\n\n    $headers  = 'MIME-Version: 1.0' . "\r\n";\n    $headers .= 'Content-type: text/html; charset="iso-8859-1"' . "\r\n";\n    $headers .= 'From: code@website.com' . "\r\n"; // Removed < and > around email\n    $headers .= 'Reply-To: ' . htmlspecialchars($email) . "\r\n"; // Quoted variable\n\n    $subject  = "[ERROR REPORT] Page: " . htmlspecialchars($page);\n\n    mail("myEmail@gmail.com", $subject, $message, $headers );\n\n    $mesg = "Thank you for your report!";\n}