$i = 0;\n$output = ""; // Initialize output as an empty string to avoid unexpected concatenation issues\nwhile ($row = mysql_fetch_array($result)) {\n    $output .= "idno" . $i . "=" . urlencode($row['usr_id']) .\n               '&' . "res" . $i . "=" . urlencode($row['responce']) .\n               '&' . "sex" . $i . "=" . urlencode($row['sex']) .\n               '&' . "com1" . $i . "=" . urlencode($row['com1']) .\n               '&' . "com2" . $i . "=" . urlencode($row['com2']);\n    if ($i < mysql_num_rows($result) - 1) { // Append newline if it's not the last row\n        $output .= "\n"; // or use '&' to keep everything in one line but separate entries\n    }\n    $i++;\n}