<?php\nfunction blurImage($src, $radius = 3, $sigma = 1.5) {\n    try {\n        $image = new Imagick($src);\n        $image->blurImage($radius, $sigma);\n        $image->writeImage('path/to/blurred_image.jpg');\n        $image->clear();\n        $image->destroy();\n    } catch (\Exception $e) {\n        echo 'Error: ' . $e->getMessage();\n    }\n}\n\n// Example usage\nblurImage("path/to/image.jpg");