<?php\n\n// Read the entire email into a string\n$emailData = file_get_contents("php://stdin");\n\n// Split headers and body\nlist($headers, $body) = explode("\r\n\r\n", $emailData, 2);\n\n// Parse the headers into an associative array\n$headersArray = [];\nforeach (explode("\r\n", $headers) as $header) {\n    list($name, $value) = explode(': ', $header, 2);\n    $headersArray[$name] = $value;\n}\n\n// Extract sender email from headers\n$fromEmail = isset($headersArray['From']) ? $headersArray['From'] : '';\n\n// Now you can work with the sender's email and the message body\necho "Sender: " . htmlspecialchars($fromEmail) . "\n";\necho "Message Body:\n" . htmlspecialchars($body);\n\n// Do your processing here...\n\n?>