// Ensure your path is correct and escape backslashes properly or use slashes\n$db_path = $_SERVER['DOCUMENT_ROOT'] . '\\WebUpdate\\' . $file_name . '.mdb';\n\ntry {\n    // Specify the OLEDB connection string for MS Access\n    $db = new PDO("odbc:Driver={Microsoft Access Driver (*.mdb, *.accdb)};Dbq=$db_path;");\n\n    echo "Connection successful";\n} catch (PDOException $e) {\n    die("Could not connect to the database $db_path :" . $e->getMessage());\n}